import React from 'react';
import {Link} from 'react-router-dom';
import {ReactComponent as Logo} from "../../assets/crown.svg";
import {auth} from '../../firebase/firebase.utils';
import './Header.scss';
import CartIcon from '../cart-icon/CartIcon';
import CartDropdown from '../cart-dropdown/CartDropdown';

import {createStructuredSelector} from 'reselect';
import {selectCartHidden} from '../../redux/cart/cartSelectors';
import {selectCurrentUser} from '../../redux/user/userSelectors';
import {selectBurgerBool} from '../../redux/burger/burgerSelectors';


import {connect} from 'react-redux';
import { toggleBurgerBool } from '../../redux/burger/burgerActions';

// ******************************************************

const Header = ({currentUser, hidden, bool, toggleBool}) => (
    <div className="header">
        <Link className="logo-container" onClick={bool ? () => toggleBool() : null} to="/">
            <Logo className="logo"/>
        </Link>
        <input type="checkbox" checked={bool} id="toggle"/>
        <div className="options">
            <Link className="option" onClick={() => toggleBool()} to="/">
                HOME
            </Link>
            <Link className="option" onClick={() => toggleBool()} to="/shop">
                SHOP
            </Link>

            <Link className="option" onClick={() => toggleBool()} to="/shop">
                CONTACT
            </Link>
            {currentUser ? <div className="option" onClick={() => auth.signOut()}>SIGN OUT</div> : <Link className="option" onClick={() => toggleBool()} to='/signin'>SIGN IN</Link>}
        </div>
        <div onClick={bool ? () => toggleBool() : null}>
            <CartIcon className="cart-icon"/>
        </div>
        <label onClick={() => toggleBool()} htmlFor="toggle">&#9776;</label>
        {
            hidden ? null : <CartDropdown/>   
        }
    </div>
);

const mapDispatchToProps = dispatch => ({
    toggleBool: dispatch(toggleBurgerBool())
});

const mapStateToProps = createStructuredSelector({
    currentUser: selectCurrentUser,
    hidden: selectCartHidden,
    bool: selectBurgerBool
});
console.log(state.bool);

export default connect(mapStateToProps, mapDispatchToProps)(Header);

